import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layout';
import Form from '../Form/Form';
import axios from 'axios';

function Income() {
    const {deleteIncome, totalIncome } = useGlobalContext();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const BASE_URL = "http://localhost:5000/api/v1/";

    const handledelete=(id)=>{
         deleteIncome(id)
    };

    useEffect(() => {
        axios.get(`${BASE_URL}get-incomes`)
            .then(response => {
                setData(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            });
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading data: {error.message}</p>;

    return (
        <IncomeStyled>
            <InnerLayout>
                <h1>Incomes</h1>
                <h2 className="total-income">Total Income: <span>${totalIncome()}</span></h2>
                <div className="income-content">
                    <div className="form-container">
                        <Form />
                    </div>
                    <div className="incomes">
                        {data.map((income) => (
                            <div key={income._id} className="income-item">
                                <h3>{income.title}</h3>
                                <p>Description: {income.description}</p>
                                <p>Amount: ${income.amount}</p>
                                <p>Date: {new Date(income.date).toLocaleDateString()}</p>
                                <p>Category: {income.category}</p>
                                <button onClick={handledelete(income._id)}>Delete</button>
                            </div>
                        ))}
                    </div>
                </div>
            </InnerLayout>
        </IncomeStyled>
    );
}

const IncomeStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income {
        display: flex;
        justify-content: center;
        align-items: center;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span {
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    .income-content {
        display: flex;
        gap: 2rem;
        .incomes {
            flex: 1;
            .income-item {
                background: #F9F9F9;
                border: 1px solid #E5E5E5;
                padding: 1rem;
                margin-bottom: 1rem;
                border-radius: 8px;
                h3 {
                    margin: 0;
                    font-size: 1.5rem;
                }
                p {
                    margin: 0.5rem 0;
                }
                button {
                    background: red;
                    color: white;
                    border: none;
                    padding: 0.5rem 1rem;
                    border-radius: 4px;
                    cursor: pointer;
                }
            }
        }
    }
`;

export default Income;
