import React, { useContext, useState } from "react"
import axios from 'axios';




const BASE_URL = 'http://localhost:5000/api/v1';



const GlobalContext = React.createContext()

export const GlobalProvider = ({children}) =>{


    const [incomes, setIncomes] = useState([])
     const [expenses, setExpenses] = useState([])
    const [setError] = useState(null)



    // Calculate incomes
    const addIncome = async (income) => {
        try {
            await axios.post(`${BASE_URL}/add-income`, addIncome);
            await getIncomes(); // Refresh list of incomes
        } catch (err) {
            setError(err.response.data.message)
        }
    };
    

const getIncomes = async () => {
    try {
        const response = await axios.get(`${BASE_URL}get-incomes`);
        setIncomes(response.data); // Assuming setIncomes is a state setter function
        console.log(response.data);
    } catch (err) {
        // Handle error if there's an issue with fetching the incomes
        setError(err.response?.data?.message || 'An error occurred while fetching incomes');
    }
};



    return(
        <GlobalContext.Provider value={{addIncome}}>
            {children}
        </GlobalContext.Provider>    
    )
}


export const useGlobalContext = () =>{
    return useContext(GlobalContext)
}
