import React, { useEffect, useCallback, useState, useMemo } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layout';
import Form from '../Form/Form';
import IncomeItem from '../IncomeItem/IncomeItem';
import { FixedSizeList as List } from 'react-window';

const Income = () => {
    const { addIncome, incomes, getIncomes, deleteIncome, totalIncome } = useGlobalContext();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchIncomes = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);
            await getIncomes();
        } catch (err) {
            setError('Failed to fetch incomes');
        } finally {
            setLoading(false);
        }
    }, [getIncomes]);

    useEffect(() => {
        fetchIncomes();
    }, [fetchIncomes]);

    // Memoize totalIncome calculation
    const totalIncomeMemo = useMemo(() => totalIncome(), [incomes]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    // Memoized IncomeItem component
    const MemoizedIncomeItem = React.memo(IncomeItem);

    return (
        <IncomeStyled>
            <InnerLayout>
                <h1>Incomes</h1>
                <h2 className="total-income">
                    Total Income: <span>${totalIncomeMemo}</span>
                </h2>
                <div className="income-content">
                    <div className="form-container">
                        <Form />
                    </div>
                    <div className="incomes">
                        <List
                            height={400}
                            itemCount={incomes.length}
                            itemSize={100} // Adjust according to your item height
                            width="100%"
                        >
                            {({ index, style }) => {
                                const income = incomes[index];
                                return (
                                    <div style={style} key={income._id}>
                                        <MemoizedIncomeItem
                                            id={income._id}
                                            title={income.title}
                                            description={income.description}
                                            amount={income.amount}
                                            date={income.date}
                                            type={income.type}
                                            category={income.category}
                                            indicatorColor="var(--color-green)"
                                            deleteItem={deleteIncome}
                                        />
                                    </div>
                                );
                            }}
                        </List>
                    </div>
                </div>
            </InnerLayout>
        </IncomeStyled>
    );
};

const IncomeStyled = styled.div`
    background-color: ${(props) => props.$bg || 'white'};
    display: flex;
    overflow: auto;
    .total-income {
        display: flex;
        justify-content: center;
        align-items: center;
        background: #fcf6f9;
        border: 2px solid #ffffff;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: 0.5rem;
        span {
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    .income-content {
        display: flex;
        gap: 2rem;
        .incomes {
            flex: 1;
        }
    }
`;

export default Income;
